function [ DV ] = Acceleration( x,y,z,c20,c22,u,ae)
%Acceleration 本函数根据一点相对于小行星的位置可以算出在只有小行星引力作用时的加速度
%   输入量位置x,y,z 球谐系数c20,c22,u = GM,ae = a;
    
DV(1,1) = (u*((ae^2*((6*c22*x*z^2*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2)/(x^2 + y^2 + z^2)^2 - (3*c20*x*z^2)/(x^2 + y^2 + z^2)^2 + (24*c22*x*z^2*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1))/(x^2 + y^2 + z^2)^2))/(x^2 + y^2 + z^2) - (2*ae^2*x*((c20*((3*z^2)/(x^2 + y^2 + z^2) - 1))/2 - 3*c22*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2))/(x^2 + y^2 + z^2)^2))/(x^2 + y^2 + z^2)^(1/2) - (u*x*((ae^2*((c20*((3*z^2)/(x^2 + y^2 + z^2) - 1))/2 - 3*c22*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2))/(x^2 + y^2 + z^2) + 1))/(x^2 + y^2 + z^2)^(3/2);
DV(2,1) = (u*((ae^2*((6*c22*y*z^2*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2)/(x^2 + y^2 + z^2)^2 - (3*c20*y*z^2)/(x^2 + y^2 + z^2)^2 + (24*c22*y*z^2*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1))/(x^2 + y^2 + z^2)^2))/(x^2 + y^2 + z^2) - (2*ae^2*y*((c20*((3*z^2)/(x^2 + y^2 + z^2) - 1))/2 - 3*c22*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2))/(x^2 + y^2 + z^2)^2))/(x^2 + y^2 + z^2)^(1/2) - (u*y*((ae^2*((c20*((3*z^2)/(x^2 + y^2 + z^2) - 1))/2 - 3*c22*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2))/(x^2 + y^2 + z^2) + 1))/(x^2 + y^2 + z^2)^(3/2);
DV(3,1) = -(u*((ae^2*(3*c22*((2*z)/(x^2 + y^2 + z^2) - (2*z^3)/(x^2 + y^2 + z^2)^2)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2 - (c20*((6*z)/(x^2 + y^2 + z^2) - (6*z^3)/(x^2 + y^2 + z^2)^2))/2 + 6*c22*((4*z)/(x^2 + y^2 + z^2) - (4*z^3)/(x^2 + y^2 + z^2)^2)*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)))/(x^2 + y^2 + z^2) + (2*ae^2*z*((c20*((3*z^2)/(x^2 + y^2 + z^2) - 1))/2 - 3*c22*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2))/(x^2 + y^2 + z^2)^2))/(x^2 + y^2 + z^2)^(1/2) - (u*z*((ae^2*((c20*((3*z^2)/(x^2 + y^2 + z^2) - 1))/2 - 3*c22*(z^2/(x^2 + y^2 + z^2) - 1)*((2*z^2)/(x^2 + y^2 + z^2) - 1)^2))/(x^2 + y^2 + z^2) + 1))/(x^2 + y^2 + z^2)^(3/2);

end

